package Array;

public class Two_d_matr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int rows = 1;
		int cols = 2;
		int[][] ans = new int[rows][cols];
		for (int i = 0; i < ans.length; i++) {
			for (int j = 0; j < ans[0].length; j++) {
				System.out.print(ans[i][j]+ " ");

			}
		}
	}

}
